import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { Alert, Container, Table } from "reactstrap";

import { api } from "../../../config";

export const ListarCompras = () => {

    const [data, setData] = useState([]);

    const [status, setStatus] = useState({
        type: '',
        message: ''
    });

    const getCompras = async () => {
        await axios.get(api+"/listacompras")
            .then((response) => {
                console.log(response.data);
                setData(response.data.compras);
            })
            .catch(() => {
                setStatus({
                    type: 'error',
                    message: "Erro: sem conexão com a API."
                })
                // console.log("Erro: sem conexão com a API.")
            })
    }


    const delCompra = async (id) => {
        console.log(id);

        const headers = {
            'content-type': 'application/json'
        }

        await axios.delete(api + "/excluircompra/" + id, { headers })
            .then((response) => {
                setStatus({
                    type: 'success',
                    message: 'Compra excluída com sucesso!'
                })
                console.log(response.data.type);
                console.log(response.data.message);
                getCompras();
            })
            .catch(() => {
                setStatus({
                    type: 'error',
                    message: 'Erro: não foi possível conectar-se a API.'
                });
            });
    };

    useEffect(() => {
        getCompras();
    }, []);

    return (
        <div>
            <Container>
                <div className="d-flex">
                    <div>
                        <h1>Visualizar informações das compras</h1>
                    </div>
                    <div className="m-auto p-2">
                        <Link to="/cadastrar-compra"
                            className="btn btn-outline-primary btn-sm">Cadastrar</Link>
                    </div>
                </div>
                {status.type === 'error' ? <Alert color="danger"> {status.message} </Alert> : ""}
                {status.type === 'success' ? <Alert color="success"> {status.message} </Alert> : ""}
                <Table striped>
                    <thead>
                        <tr>
                            <th>Id da Compra</th>
                            <th>Id do Cliente</th>
                            <th>Data da Compra</th>
                            <th>Ação</th>
                        </tr>
                    </thead>
                    <tbody>
                        {data.map(enem => (
                            <tr key={enem.id}>
                                <td>{enem.id}</td>
                                <td>{enem.ClienteId}</td>
                                <td>{enem.dataPedido}</td>
                                <td className="text-center/">
                                    <Link to={"/compraitens/"+enem.id}
                                        className="btn btn-outline-primary btn-sm">Consultar</Link>
                                    <Link to={"/editar-compra/" + enem.id}
                                        className="btn btn-outline-warning btn-sm">Editar</Link>
                                    <span className="btn btn-outline-danger btn-sm"
                                        onClick={() => delCompra(enem.id)}>Excluir</span>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </Table>
            </Container>
        </div>
    );
};
