import axios from "axios";
import { useEffect } from "react";
import { useState } from "react/cjs/react.development";
import { Alert, Button, Container, Form, FormGroup, Input, Label } from "reactstrap";
import { api } from "../../../config";

export const EditarCliente = (props) => {

    const [id, setId] = useState(props.match.params.id);
    const [data, setData] = useState('');
    const [ClienteId, setClienteId] = useState('');


    const [clienteDados, setClienteDados] = useState({
        nome: '',
        endereco: '',
        cidade: '',
        uf: '',
        nascimento: '',
        clienteDesde: ''
    })


    const [status, setStatus] = useState({
        type: '',
        message: ''
    });

    const editCliente = async (idCliente) => {
        console.log(idCliente);

        const headers = {
            'content-type': 'application/json'
        }

        await axios.put(`${api}/atualizaclientes`, { clienteDados }, { headers })
            .then((response) => {
                setStatus({
                    type: 'success',
                    message: response.data.message
                })
            }).catch(() => {
                setStatus({
                    type: 'error',
                    message: "Não foi possível acessar API."
                });
            });
    };


    const editPedido = async e => {
        e.preventDefault();

        const headers = {
            'Content-Type': 'application/json'
        };

        await axios.put(api + "/pedido/" + id,
            { id, data, ClienteId }, { headers })
            .then((response) => {
                setStatus({
                    type: 'success',
                    message: 'Alteração feita com sucesso.'
                })
                console.log(response.data.type);
                console.log(response.data.message);
            })
            .catch(() => {
                setStatus({
                    type: 'error',
                    message: 'Não foi possível acessar a API.'
                });
            });
    };

    useEffect(() => {
        const getCliente = async () => {
            await axios(api + "/pedido/" + id)
                .then((response) => {
                    setId(response.data.pedido.id);
                    setData(response.data.pedido.data);
                    setClienteId(response.data.pedido.ClienteId);
                })
                .catch(() => {
                    console.log("Erro: não foi possível se conectar a API.")
                })
        }
        getCliente();
    }, [id]);

    return (
        <div>
            <Container>
                <div className="d-flex">
                    <div className="m-auto p-2">
                        <h1>Editar Cliente</h1>
                    </div>
                    <div className="p-2">
                        <a href="/listar-cliente" className="btn btn-outline-success btn-sm">Cliente</a>
                    </div>
                    <hr className="m-1" />
                    {status.type === 'error' ? <Alert color="danger">
                        {status.message}</Alert> : " "}
                    {status.type === 'success' ? <Alert color="success">
                        {status.message}</Alert> : " "}
                </div>
                <Form className="p-2" onSubmit={editCliente}>
                    <FormGroup className="p-2">
                        <Label>
                            Nome do Cliente
                        </Label>
                        <Input
                            name="nome"
                            placeholder="nome do cliente"
                            type="text"
                            defaultValue={nome}
                        />
                    </FormGroup>
                    <FormGroup className="p-2">
                        <Label>
                            Data de Nascimento
                        </Label>
                        <Input
                            name="nascimento"
                            placeholder="data de nascimento"
                            type="text"
                            value={nascimento}
                            onChange={e => setData(e.target.value)}
                        />
                    </FormGroup>
                    <FormGroup className="p-2">
                        <Label>
                            Endereço
                        </Label>
                        <Input
                            name="endereco"
                            placeholder="endereco do cliente"
                            type="text"
                            defaultValue={ClienteId}
                        />
                    </FormGroup>
                    <FormGroup className="p-2">
                        <Label>
                            Cidade
                        </Label>
                        <Input
                            name="cidade"
                            placeholder="cidade do cliente"
                            type="text"
                            defaultValue={ClienteId}
                        />
                    </FormGroup>
                    <FormGroup className="p-2">
                        <Label>
                            UF
                        </Label>
                        <Input
                            name="uf"
                            placeholder="uf do cliente"
                            type="text"
                            defaultValue={ClienteId}
                        />
                    </FormGroup>
                    <Button type="submit" outline color="warning">
                        Salvar
                    </Button>
                    <Button type="reset" outline color="primary">
                        Limpar
                    </Button>
                </Form>
            </Container >
        </div >
    );
};
